void Player::SendForceUpdateToClient()
{
    if (!CanContact())
        return;

    UpdateData upd(GetMapId());
    WorldPacket packet;

    BuildValuesUpdateBlockForPlayer(&upd, this);

    if (upd.BuildPacket(&packet))
        GetSession()->SendPacket(&packet);
}

